import React, { useState, useEffect } from 'react';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
  width: '100%',
  height: '400px',
};

const LocationPage = (props) => {
  const [userLocation, setUserLocation] = useState({ lat: 4.7110, lng: -74.0721 }); 

  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setUserLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude
        });
      },
      (error) => {
        console.error(error);
      }
    );
  }, []);

  return (
    <Map
      google={props.google}
      zoom={12}
      style={mapStyles}
      initialCenter={userLocation}
    >
      {/* Marcador para la ubicación actual del usuario */}
      <Marker position={userLocation} title="Tu Ubicación" />
      {/* Marcadores para los jardines */}
      {jardines.map((jardin, index) => (
        <Marker
          key={index}
          position={{ lat: jardin.lat, lng: jardin.lng }}
          title={jardin.nombre} // Asumiendo que 'nombre' es una propiedad en tus datos de jardines
        />
      ))}
    </Map>
  );
};

export default GoogleApiWrapper({
  apiKey: 'AIzaSyAL0LqR3PA49z8gmgYbWPSpJRdAWTtbuaI' // Asegúrate de reemplazar con tu clave API real
})(LocationPage);
